
public class TypeChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		문자형 char
//		char 타입
//		한 문자
		
		char ch = 'A';
		
		System.out.println(ch);
		
		System.out.println(ch + 1);
		
//		ch = 'ㅇ';
//		String str = "";
//		문자
//		문자열
		
		
//		특수문자
		System.out.print('\t');
		
		System.out.print("123\t"); 
		
		System.out.print("\n"); 
		
		System.out.println("\""+"안녕"+"\"");
	}

}
