
public class Ch3OperatorEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		대입 연산자
		
//		변수와 같은 저장공간에 값 또는 수식의 연산 결과를 저장하는데 사용된다
		
		int sum = 0;
		
		sum = sum +1;
		System.out.println(sum);
		
		sum += 1; // sum = sum +1
		System.out.println(sum);
		
		sum -= 10; // sum = sum - 10
		System.out.println(sum);
		
		
	}

}
